package specs

import org.scalatest.FeatureSpec
import org.scalatest.Matchers._
import weeks.DNAString._
import weeks.Week4._
import weeks.{DNAMotif, DNAString}

import scala.annotation.tailrec
import scala.io.Source

class Week4Spec extends FeatureSpec {
  feature("compositionKmers") {
    scenario("example") {
      val text = DNAString("CAATCCAAC")
      val k = 5
      compositionKmers(text, k) shouldBe DNAMotif("AATCC\n     ATCCA\n     CAATC\n     CCAAC\n     TCCAA").value
    }

    scenario("extra dataset") {
      val text = DNAString("")
      val k = 100
      val sf = Source.fromFile("src/main/resources/compositionKmersExtraDatasetSolution.txt")
      val st = sf.getLines().mkString("\n")
      val expectedResult = DNAMotif.from(st).get.value
      compositionKmers(text, k) shouldBe expectedResult
    }

    scenario("interactive quiz") {
      val text = DNAString("")
      val k = 100
      val sf = Source.fromFile("src/main/resources/compositionKmersInteractiveQuizSolution.txt")
      val st = sf.getLines().mkString("\n")
      val expectedResult = DNAMotif.from(st).get.value
      compositionKmers(text, k) shouldBe expectedResult
    }
  }

  feature("stringSpelledByGenomePath") {
    scenario("example") {
      val path = DNAMotif("ACCGA\nCCGAA\nCGAAG\nGAAGC\nAAGCT")
      stringSpelledByGenomePath(path) shouldBe DNAString("ACCGAAGCT")
    }

    scenario("extra dataset") {
      val path = DNAMotif.from(Source.fromFile("src/main/resources/stringSpelledByGenomePathExtraDatasetInput.txt").getLines().mkString("\n")).get
      stringSpelledByGenomePath(path) shouldBe DNAString("")
    }

    scenario("interactive quiz") {
      val path = DNAMotif.from(Source.fromFile("src/main/resources/stringSpelledByGenomePathInterativeQuizInput.txt").getLines().mkString("\n")).get
      stringSpelledByGenomePath(path) shouldBe DNAString("")
    }

    scenario("quiz") {
      val path = DNAMotif("CAAA\nAAAT\nAATG\nATGC\nTGCA\nGCAT\nCATA\nATAC\nTACG\nACGC\nCGCT\nGCTC\nCTCA\nTCAT\nCATC\nATCA\nTCAC\nCACC\nACCC\nCCCA\nCCAG")
      val result = stringSpelledByGenomePath(path)
      result shouldBe DNAString("CAAATGCATACGCTCATCACCCAG")
    }
  }

  feature("overlap") {
    scenario("example") {
      val patterns = DNAMotif("ATGCG\nGCATG\nCATGC\nAGGCA\nGGCAT")
      val result: IndexedSeq[(DNAString, DNAString)] = IndexedSeq(
        DNAString("AGGCA") → DNAString("GGCAT"),
        DNAString("CATGC") → DNAString("ATGCG"),
        DNAString("GCATG") → DNAString("CATGC"),
        DNAString("GGCAT") → DNAString("GCATG")
      )
      overlap(patterns) shouldBe result
    }

    lazy val regex = """(?i)([ACGT]+) -> ([ACGT]+)""".r
    def load(fn: String): IndexedSeq[(DNAString, DNAString)] = {
      Source.fromFile(fn).getLines().map {
        case regex(left, right) ⇒
          DNAString.unsafeFrom(left) → DNAString.unsafeFrom(right)
      }.toIndexedSeq
    }

    scenario("extra dataset") {
      val patterns = DNAMotif.from(Source.fromFile("src/main/resources/overlapExtraDatasetInput.txt").getLines().mkString("\n")).get
      val expectedResult = load("src/main/resources/overlapExtraDatasetOutput.txt")
      overlap(patterns) shouldBe expectedResult
    }

    scenario("interactive quiz") {
      val patterns = DNAMotif.from(Source.fromFile("src/main/resources/overlapInterativeQuizInput.txt").getLines().mkString("\n")).get
      val expectedResult = load("src/main/resources/overlapInterativeQuizOutput.txt")
      overlap(patterns) shouldBe expectedResult
    }
  }

  def loadGraphOfDNAStrings(fn: String): Map[DNAString, IndexedSeq[DNAString]] = {
    val regex = """(?i)([ACGT]+) -> ([ACGT,]+)""".r
    Map(Source.fromFile(fn).getLines().map {
      case regex(left, right) ⇒
        DNAString.unsafeFrom(left) → right.split(',').toIndexedSeq.map(DNAString.unsafeFrom(_))
    }.toSeq: _*)
  }

  feature("deBruijn") {
    scenario("example") {
      val text  = DNAString("AAGATTCTCTAAGA")
      val k = 4
      deBruijn(text, k) shouldBe Map(
        DNAString("AAG") → IndexedSeq(DNAString("AGA"), DNAString("AGA")),
        DNAString("AGA") → IndexedSeq(DNAString("GAT")),
        DNAString("ATT") → IndexedSeq(DNAString("TTC")),
        DNAString("CTA") → IndexedSeq(DNAString("TAA")),
        DNAString("CTC") → IndexedSeq(DNAString("TCT")),
        DNAString("GAT") → IndexedSeq(DNAString("ATT")),
        DNAString("TAA") → IndexedSeq(DNAString("AAG")),
        DNAString("TCT") → IndexedSeq(DNAString("CTA"), DNAString("CTC")),
        DNAString("TTC") → IndexedSeq(DNAString("TCT"))
      )
    }

    scenario("extra dataset") {
      val text: DNAString = ""
      val k = 12
      val expectedResult = loadGraphOfDNAStrings("src/main/resources/deBruijnExtraDatasetSolution.txt")
      deBruijn(text, k) shouldBe expectedResult
    }

    scenario("interactive quiz") {
      val text: DNAString = ""
      val k = 12
      val expectedResult = loadGraphOfDNAStrings("src/main/resources/deBruijnInteractiveQuizSolution.txt")
      val result = deBruijn(text, k)
      result shouldBe expectedResult
    }
  }

  feature("deBruijnFromKmers") {
    scenario("example") {
      val kMers  = DNAMotif("GAGG\nCAGG\nGGGG\nGGGA\nCAGG\nAGGG\nGGAG")
      deBruijnFromKmers(kMers) shouldBe Map(
        DNAString("AGG") → IndexedSeq(DNAString("GGG")),
        DNAString("CAG") → IndexedSeq(DNAString("AGG"), DNAString("AGG")),
        DNAString("GAG") → IndexedSeq(DNAString("AGG")),
        DNAString("GGA") → IndexedSeq(DNAString("GAG")),
        DNAString("GGG") → IndexedSeq(DNAString("GGA"), DNAString("GGG"))
      )
    }

    scenario("extra dataset") {
      val kMers = DNAMotif.from(Source.fromFile("src/main/resources/deBruijnFromKmersExtraDatasetInput.txt").getLines().mkString("\n")).get
      val expectedResult = loadGraphOfDNAStrings("src/main/resources/deBruijnFromKmersExtraDatasetSolution.txt")
      val result = deBruijnFromKmers(kMers)
      result shouldBe expectedResult
    }

    scenario("interactive quiz") {
      val kMers = DNAMotif.from(Source.fromFile("src/main/resources/deBruijnFromKmersInteractiveQuizInput.txt").getLines().mkString("\n")).get
      val expectedResult = loadGraphOfDNAStrings("src/main/resources/deBruijnFromKmersInteractiveQuizSolution.txt")
      val result = deBruijnFromKmers(kMers)
      result shouldBe expectedResult
    }
  }

  def loadGraphOfNodes(fn: String): Map[Int, Seq[Int]] = {
    val regex = """(?i)(\d+) -> ([\d,]+)""".r
    Map(Source.fromFile(fn).getLines().map {
      case regex(left, right) ⇒
        left.toInt → right.split(',').toSeq.map(_.toInt)
    }.toSeq: _*).withDefaultValue(Seq.empty[Int])
  }

  feature("eulerianCycle") {
    scenario("example") {
      val graph = Map(
        0 → Seq(3),
        1 → Seq(0),
        2 → Seq(1, 6),
        3 → Seq(2),
        4 → Seq(2),
        5 → Seq(4),
        6 → Seq(5, 8),
        7 → Seq(9),
        8 → Seq(7),
        9 → Seq(6)
      )
      eulerianCycle(graph) shouldBe IndexedSeq(0, 3, 2, 6, 8, 7, 9, 6, 5, 4, 2, 1, 0)
    }

    scenario("extra dataset") {
      val graph = loadGraphOfNodes("src/main/resources/eulerianCycleExtraDatasetInput.txt")
      // /!\ since the eulerianCycle finds A path, needed to modify the expected result
