  @tailrec private def sameAs[A](l: Traversable[A], r: Traversable[A]): Boolean = {
    import scala.language.postfixOps
    if (l.isEmpty) r.isEmpty
    else {
      val (e, f) = r span (l.head !=)
      if (f.isEmpty) false
      else sameAs(l.tail, e ++ f.tail)
    }
  }

  feature("maximalNonBranchingPaths") {
    scenario("example") {
      val graph = Map(
        1 → Seq(2),
        2 → Seq(3),
        3 → Seq(4, 5),
        6 → Seq(7),
        7 → Seq(6)
      )
      maximalNonBranchingPaths(graph) shouldBe Seq(Seq(1, 2, 3), Seq(3, 4), Seq(3, 5), Seq(6, 7, 6))
    }

    def loadPaths(fn: String): Seq[Seq[Int]] = {
      Source.fromFile(fn).getLines().map { line ⇒
        line.split(" -> ").map(_.toInt).toSeq
      }.toSeq
    }

    scenario("extra dataset") {
      val graph = loadGraphOfNodes("src/main/resources/maximumNonBranchingPathsExtraDatasetInput.txt")
      val expectedResult = loadPaths("src/main/resources/maximumNonBranchingPathsExtraDatasetOutput.txt")
      val result = maximalNonBranchingPaths(graph)
      assert(sameAs(result, expectedResult))
    }

    scenario("interactive quiz") {
      val graph = loadGraphOfNodes("src/main/resources/maximumNonBranchingPathsInteractiveQuizInput.txt")
      val expectedResult = loadPaths("src/main/resources/maximumNonBranchingPathsInteractiveQuizOutput.txt")
      val result = maximalNonBranchingPaths(graph)
      assert(sameAs(result, expectedResult))
    }
  }

