    scenario("example") {
      val graph = IndexedSeq(Seq(3), Seq(0), Seq(1, 6), Seq(2), Seq(2), Seq(4), Seq(5, 8), Seq(9), Seq(7), Seq(6))
      eulerianCycle(graph) shouldBe IndexedSeq(6, 8, 7, 9, 6, 5, 4, 2, 1, 0, 3, 2, 6)
